import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
    const [user, setUser] = useState(null);
    const userId = 'some_user_id'; // Replace with the actual user ID

    useEffect(() => {
        const fetchUserProfile = async () => {
            const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
            setUser(response.data);
        };
        fetchUserProfile();
    }, [userId]);

    return (
        <div className="container">
            {user ? (
                <div className="card mt-3 shadow border-info">
                    <img src={user.imageUrl} className="card-img-top" alt={user.username} />
                    <div className="card-body">
                        <h5 className="card-title text-primary">{user.username}</h5>
                        <p className="card-text"><strong>Email:</strong> {user.email}</p>
                    </div>
                </div>
            ) : (
                <p>Loading user profile...</p>
            )}
        </div>
    );
}

export default UserProfile;
