import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const JobList = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const response = await axios.get('http://localhost:5000/api/jobs');
      setJobs(response.data);
    };

    fetchJobs();
  }, []);

  const handleApply = async (job) => {
    const userId = localStorage.getItem('userId');

    if (!userId) {
      alert('You need to be logged in to apply for this job.');
      return;
    }

    try {
      await axios.post(`http://localhost:5000/api/jobs/${job._id}/apply`, { userId });
      alert('Successfully applied for the job!');
      window.location.reload(); // Reload the page to see updated applied status
    } catch (error) {
      console.error('Error applying for the job:', error);
      alert('Failed to apply for the job.');
    }
  };

  return (
    <div>
      <h2>Available Jobs</h2>
      <div className="row">
        {jobs.map((job) => (
          <div className="col-md-4 mb-4" key={job._id}>
            <div className="card">
              <img src={job.imageUrl} className="card-img-top" alt={job.title} />
              <div className="card-body">
                <h5 className="card-title">{job.title}</h5>
                <p className="card-text">{job.description}</p>
                <p className="card-text"><strong>Company:</strong> {job.company}</p>
                <p className="card-text"><strong>Location:</strong> {job.location}</p>
                <button className="btn btn-primary" onClick={() => handleApply(job)}>Apply</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default JobList;
