import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function JobList() {
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        const fetchJobs = async () => {
            const response = await axios.get('http://localhost:5000/api/jobs');
            setJobs(response.data);
        };
        fetchJobs();
    }, []);

    return (
        <div className="row">
            {jobs.map(job => (
                <div className="col-md-4" key={job._id}>
                    <div className="card mb-3 shadow-lg border-success">
                        <img src={job.imageUrl} className="card-img-top" alt={job.title} />
                        <div className="card-body">
                            <h5 className="card-title text-primary">{job.title}</h5>
                            <p className="card-text">{job.description}</p>
                            <Link to={`/jobs/${job._id}`} className="btn btn-outline-success">View Details</Link>
                            <button className="btn btn-outline-warning ms-2">Apply</button>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default JobList;
